func:
// Recebe os dados da requisição POST
var dados = msg.payload;

// Altera a variável booleana com base nos dados recebidos
if (dados.valor === "on") {
    global.set("minha_variavel", true);
} else {
    global.set("minha_variavel", false);
}

// Envia uma resposta à requisição
msg.payload = dados.valor;
return msg;
